//packages
import React, { useState, useEffect } from "react";
import { Link, useNavigation, useLocation } from "react-router-dom";
import { AnimatePresence } from "framer-motion";
//styling
import styles from "./style.module.scss";
//components
import Magnetic from '../../commonComponents/magnetic/index';
import Preloader from "../../commonComponents/preloader";

const Navbar = () => {
  const [showPreloader, setShowPreloader] = useState(false);
  const [preloaderWord, setPreloaderWord] = useState("");
  const [nextRoute, setNextRoute] = useState("");
  const navigation = useNavigation();
  const location = useLocation();

  const handleLinkClick = (event, word, path) => {
    event.preventDefault();
    setPreloaderWord(word);
    setShowPreloader(true);
    setNextRoute(path);

    // Optional: hide the preloader after some time (e.g., 2 seconds)
    setTimeout(() => {
      setShowPreloader(false);
      window.scrollTo(0, 0);
    }, 1250);
  };

  useEffect(() => {
    if (!showPreloader && nextRoute) {
      setTimeout(() => {
        navigation.navigate(nextRoute);
      }, 700);  // Assuming it takes 700ms for the preloader to cover the screen
    }
  }, [showPreloader, nextRoute, navigation]);

  return (
    <div className={styles.Navbar}>
      <AnimatePresence>
        {showPreloader && <Preloader wordsList={[preloaderWord]} />}
      </AnimatePresence>
      <Magnetic>
        <Link to="/" onClick={(event) => handleLinkClick(event, 'Home', "/")}>Home</Link>
      </Magnetic>
      <Magnetic>
        <Link to="/about" onClick={(event) => handleLinkClick(event, 'About', "/about")}>About</Link>
      </Magnetic>
      <Magnetic>
        <Link to="/gallery" onClick={(event) => handleLinkClick(event, 'Gallery', "/gallery")}>Gallery</Link>
      </Magnetic>
      <Magnetic>
        <Link to="/contact" onClick={(event) => handleLinkClick(event, 'Contact', "/contact")}>Contact</Link>
      </Magnetic>
    </div>
  );
};

export default Navbar;
