import React, { useState } from 'react';
import emailjs from 'emailjs-com';

const Contact = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    surname: '',
    email: '',
    phoneNumber: '',
    typeOfVideo: '',
    videoDesc: '',
    budget: 0,
    timelineStart: '',
    timelineEnd: '',
    filesAndInspiration: null
  });

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Send the email using emailjs
    emailjs.sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', e.target, 'YOUR_USER_ID')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  };

  return (
    <>
      {/* Quick contact buttons */}
      <button onClick={() => window.location.href = "mailto:aja.visionss@gmail.com?subject=Quick Contact"}>Email</button>
      <button onClick={() => window.location.href = "tel:+07999039535"}>Phone</button>
      <button onClick={() => window.location.href = "https://www.instagram.com/direct/t/17844959589023407"}>Instagram DM</button>
      {/* Add the button for Facebook DM here... */}

      {/* Enquiry form */}
      <form onSubmit={handleSubmit}>
        <input type="text" name="firstName" onChange={handleInputChange} placeholder="FIRST NAME" />
        {/* ... other fields here ... */}
        <select name="typeOfVideo" onChange={handleInputChange}>
          <option value="option1">Option 1</option>
          <option value="option2">Option 2</option>
          {/* ... other options here ... */}
        </select>
        <textarea name="videoDesc" onChange={handleInputChange} placeholder="VIDEO DESC"></textarea>
        {/* ... other fields here ... */}
        <button type="submit">Action</button>
      </form>
    </>
  );
}

export default Contact;
