import './App.css';
import React from "react";
import { useState, useEffect } from 'react';
import { AnimatePresence } from 'framer-motion';
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Home from "./pages/home/index";
import About from "./pages/about/index";
import Gallery from "./pages/gallery/index";
import Contact from "./pages/contact/index";
import Navbar from "./components/navbar/index";
import Preloader from './commonComponents/preloader';

function App() {

  const initialRoute = window.location.pathname;
  // Determine the initial preloader word based on the route
  let initialWord;
  switch (initialRoute) {
    case '/about':
      initialWord = 'About';
      break;
    case '/gallery':
      initialWord = 'Gallery';
      break;
    case '/contact':
      initialWord = 'Contact';
      break;
    default:
      initialWord = 'Home'; // Or whatever your default word is
  }

  const [isLoading, setIsLoading] = useState(true);
  const [preloaderWord, setPreloaderWord] = useState(initialWord);

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
      document.body.style.cursor = 'default';
      window.scrollTo(0, 0);
    }, 2000);
  }, []);

  return (
    <div className="App">
      <AnimatePresence mode='wait'>
        {isLoading && <Preloader wordsList={[preloaderWord]} />}
      </AnimatePresence>
      <Router>
        <Navbar />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/gallery" element={<Gallery />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
