import './App.css';
import React, { useEffect } from "react";
import { useState } from 'react';
import { AnimatePresence } from 'framer-motion';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from "react-router-dom";
import Home from "./pages/home/index";
import About from "./pages/about/index";
import Gallery from "./pages/gallery/index";
import Contact from "./pages/contact/index";
import Navbar from "./components/navbar/index";
import Preloader from './commonComponents/preloader';

function MainContent() {
  const [showPreloader, setShowPreloader] = useState(false);
  const [preloaderWord, setPreloaderWord] = useState("");
  const navigate = useNavigate();
  const location = useLocation();

  const handleNavigation = (path, word) => {
    setPreloaderWord(word);
    setShowPreloader(true);

    setTimeout(() => {
      navigate(path);
      setShowPreloader(false);
    }, 1250);
  };

  return (
    <AnimatePresence mode='wait'>
      {showPreloader && <Preloader wordsList={[preloaderWord]} />}
      <Navbar handleNavigation={handleNavigation} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/gallery" element={<Gallery />} />
        <Route path="/contact" element={<Contact />} />
      </Routes>
    </AnimatePresence>
  );
}

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [isFirstTimeViewing, setIsFirstTimeViewing] = useState(false);

  useEffect(() => {
    const initialRoute = window.location.pathname;
    if (initialRoute === "/") {
      setIsFirstTimeViewing(true);
    }
    setTimeout(() => {
      setIsLoading(false);
      setIsFirstTimeViewing(false);
      document.body.style.cursor = 'default';
      window.scrollTo(0, 0);
    }, 2000);
  }, []);

  return (
    <Router>
      <div className="App">
        <AnimatePresence mode='wait'>
          {isFirstTimeViewing && isLoading && <Preloader />}
        </AnimatePresence>
        <MainContent />
      </div>
    </Router>
  );
}

export default App;
