//packages
import React, { useState } from "react";
import { ReactDOM } from "react";
import { Link } from "react-router-dom";
import { AnimatePresence } from "framer-motion";
//styling
import styles from "./style.module.scss";
//components
import Magnetic from '../../commonComponents/magnetic/index';
import Preloader from "../../commonComponents/preloader";

const Navbar = () => {
  const [showPreloader, setShowPreloader] = useState(false);
  const [preloaderWord, setPreloaderWord] = useState("");

  const handleLinkClick = (word) => {
    setPreloaderWord(word);
    setShowPreloader(true);

    // Optional: hide the preloader after some time (e.g., 2 seconds)
    setTimeout(() => {
      setShowPreloader(false);
      window.scrollTo(0,0);
    }, 1000);
  };

  return (
    <div className={styles.Navbar}>
        <AnimatePresence>
            {showPreloader && ReactDOM.createPortal(<Preloader wordsList={[preloaderWord]} />, document.body)}
        </AnimatePresence>
        <Magnetic>
            <Link to="/" onClick={() => handleLinkClick('Home')}>Home</Link>
        </Magnetic>
        <Magnetic>
            <Link to="/about" onClick={() => handleLinkClick('About')}>About</Link>
        </Magnetic>
        <Magnetic>
            <Link to="/gallery" onClick={() => handleLinkClick('Gallery')}>Gallery</Link>
        </Magnetic>
        <Magnetic>
            <Link to="/contact" onClick={() => handleLinkClick('Contact')}>Contact</Link>
        </Magnetic>
    </div>
  );
};

export default Navbar;
